sys_script_client.script:
function onLoad() {
	var fieldName = 'nationality'; // Replace with your actual field name

    // 1. Get the field element
    var control = g_form.getControl(fieldName);

    if (control) {
        // 2. Find the button that has the 'data-ref-id' or ID matching the lookup
        // We look for a button that is a sibling or child of the parent container
        var parent = control.closest('div.form-field-element');
        if (!parent) parent = control.parentElement;

        var lookupBtn = parent.querySelector('button[id*="lookup"]');

        if (lookupBtn) {
            lookupBtn.style.setProperty('display', 'none', 'important');
        }
    }
}
