sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    const results = api.data.look_up_people.results || [];
    const peopleSearchText = api.state.peopleSearchText ? api.state.peopleSearchText.toLowerCase() : "";

    return results
        .filter(record => record.first_name_th.value.toLowerCase().includes(peopleSearchText) || record.last_name_th.value.toLowerCase().includes(peopleSearchText))
        .map(record => {
            return {
                "id": record._row_data.uniqueValue,
                "label": `${record.first_name_th.displayValue} ${record.last_name_th.displayValue}`,
                "sublabel": record.email.displayValue
            };
        });
}
