sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    api.setState('adopteeFormSubmitted', true);

    const formData = api.state.adopteeForm;
    let requiredFields = ['title_name', 'first_name_th', 'last_name_th', 'first_name_en', 'last_name_en', 'nationality'];

    const nationality = api.data.look_up_nationality.results.find( item => item._row_data.uniqueValue === formData.nationality);
    if (nationality.nationality_th.value === 'ไทย') {
        requiredFields = [...requiredFields, 'id_card'];
    } else {
        requiredFields = [...requiredFields, 'passport_no'];
    }

    const invalidFields = requiredFields.filter(field => !formData[field]);
    if (invalidFields.length > 0) {
        return;
    }

    const adopteeList = api.state.adopteeList;
    let currentAdopteeList = [
        ...adopteeList,
    ];
    let currentAdoptee = {};

    // new item
    if (!formData.sys_id) {
        currentAdoptee = {
            _row_data: {
                uniqueValue: `NEW_${new Date().getTime()}`,
            },
            title_name: {
                value: formData.title_name,
            },
            first_name_th: {
                displayValue: formData.first_name_th,
                value: formData.first_name_th
            },
            first_name_en: {
                displayValue: formData.first_name_en,
                value: formData.first_name_en
            },
            last_name_th: {
                displayValue: formData.last_name_th,
                value: formData.last_name_th
            },
            last_name_en: {
                displayValue: formData.last_name_en,
                value: formData.last_name_en
            },
            nationality: {
                value: formData.nationality
            },
            id_card: {
                displayValue: formData.id_card,
                value: formData.id_card
            },
            passport_no: {
                displayValue: formData.passport_no,
                value: formData.passport_no
            },
            oldNameList: [],
            operation: "insert",
        };
        currentAdopteeList = [
            ...currentAdopteeList,
            currentAdoptee,
        ];
    } else {
        currentAdopteeList = currentAdopteeList.map(item => {
            if (item._row_data.uniqueValue === formData.sys_id) {
                return {
                    _row_data: {
                        uniqueValue: formData.sys_id,
                    },
                    title_name: {
                        value: formData.title_name,
                    },
                    first_name_th: {
                        displayValue: formData.first_name_th,
                        value: formData.first_name_th
                    },
                    first_name_en: {
                        displayValue: formData.first_name_en,
                        value: formData.first_name_en
                    },
                    last_name_th: {
                        displayValue: formData.last_name_th,
                        value: formData.last_name_th
                    },
                    last_name_en: {
                        displayValue: formData.last_name_en,
                        value: formData.last_name_en
                    },
                    nationality: {
                        value: formData.nationality
                    },
                    id_card: {
                        displayValue: formData.id_card,
                        value: formData.id_card
                    },
                    passport_no: {
                        displayValue: formData.passport_no,
                        value: formData.passport_no
                    },
                    oldNameList: [...item.oldNameList],
                    operation: formData.sys_id.includes("NEW_") ? "insert" : "update",
                };
            }
            return item;
        });
    }

    api.setState('adopteeList', currentAdopteeList);
    api.setState('adopteeFormSubmitted', false);
    api.setState('adopteeForm', {
        "sys_id": "",
        "first_name_en": "",
        "first_name_th": "",
        "last_name_en": "",
        "last_name_th": "",
        "title_name": "",
        "nationality": "",
        "id_card": "",
        "passport_no": "",
    });
    api.emit('MODAL_SELECTED', {
        modalId: 'adoptee_modal_',
        showModal: false
    });
}
