sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const payload = event.payload || {};

    const prev = Array.isArray(api.state.documents) ? api.state.documents : [];
    console.log("onupload ", payload);
    const newDoc = {
        doc_file: payload.file_name,
        doc_id: payload.sys_id,
        table_sys_id:payload.table_sys_id,
        doc_type: api.state.selectedDocType,
        doc_type_id: api.state.selectedDocTypeId,
    };

    // ✅ สร้าง array ใหม่ (ไม่ push ของเดิม)
    const next = [...prev, newDoc];

    api.setState('documents', next);

    console.log('documents updated', next);
}
