sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const initData = api.data.look_up_a_single_request.result;
    api.setState('request', {
        ...api.state.request,
        authorized_name: initData.authorized_name.value,
        authorized_position: initData.authorized_position.value,
        company: initData.company.value,
        company_corporation: initData.company_corporation.value,
        coordinator: initData.coordinator,
        registered_capital: initData.registered_capital.value,
        request_type: initData.request_type.value,
        shareholder_capital: initData.shareholder_capital.value,
        status: initData.status.value,
        title: initData.title.value,
    });
}
