sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

    const { oldNameList, oldNameSelectedSysId} = api.state;
    let currentOldNameList = [...oldNameList];

    const objSelected = oldNameList.find(item => item._row_data.uniqueValue === oldNameSelectedSysId);
    if (objSelected.operation === 'insert') {
        currentOldNameList = currentOldNameList.filter( item => item._row_data.uniqueValue !== oldNameSelectedSysId);
    } else {
        currentOldNameList = oldNameList.map(item => {
            if (item._row_data.uniqueValue === oldNameSelectedSysId) {
                return {
                    ...item,
                    operation: "delete" ,
                };
            }
            return item;
        });
    }
    api.setState('oldNameList', currentOldNameList);
}
