sys_ux_client_script.script:
function handler({ api, event }) {
  console.log('=== checkbox event fired ===');

  const checked = event.payload.value === true;
  const row = event.context.item.value;
  const sysId = row._row_data.uniqueValue;

  console.log('checked =', checked);
  console.log('sysId =', sysId);

  if (!sysId) {
    console.warn('❌ sysId not found');
    return;
  }

  // ✅ JSON state อาจเป็น null ได้ ต้อง normalize เป็น array ก่อน
  const raw = api.state && api.state.selectedIds;
  const selectedIds = Array.isArray(raw) ? raw : [];

  console.log('selectedIds (before) =', selectedIds);

  let nextSelectedIds;

  if (checked) {
    nextSelectedIds = selectedIds.indexOf(sysId) === -1
      ? selectedIds.concat(sysId)
      : selectedIds;
  } else {
    nextSelectedIds = selectedIds.filter(id => id !== sysId);
  }

  api.setState('selectedIds', nextSelectedIds);

  console.log('selectedIds (after) =', nextSelectedIds);
  console.log('============================');
}
