sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

    const { spouseList, spouseSelectedSysId, spouseOldNameSelectedSysId} = api.state;
    const currentSpouseObj = spouseList.find(item => item._row_data.uniqueValue === spouseSelectedSysId);
    let currentSpouseOlaNameList = [...currentSpouseObj.oldNameList];
    const objSelected = currentSpouseOlaNameList.find(item => item._row_data.uniqueValue === spouseOldNameSelectedSysId);
    if (objSelected.operation === 'insert') {
        currentSpouseOlaNameList = currentSpouseOlaNameList.filter( item => item._row_data.uniqueValue !== spouseOldNameSelectedSysId);
    } else {
        currentSpouseOlaNameList = currentSpouseOlaNameList.map(item => {
            if (item._row_data.uniqueValue === spouseOldNameSelectedSysId) {
                return {
                    ...item,
                    operation: "delete" ,
                };
            }
            return item;
        });
    }

    const currentSpouseList = spouseList.map( item => {
        if (item._row_data.uniqueValue === spouseSelectedSysId) {
            return {
                ...item,
                oldNameList: currentSpouseOlaNameList,
            }
        }
        return item;
    });

    api.setState('spouseList', currentSpouseList);
}
