sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    api.setState('adopteeOldNameFormSubmitted', true);

    const formData = api.state.adopteeOldNameForm;
    let requiredFields = ['title_name', 'first_name_th', 'last_name_th', 'first_name_en', 'last_name_en'];

    const invalidFields = requiredFields.filter(field => !formData[field]);
    if (invalidFields.length > 0) {
        return;
    }

    const { adopteeList, adopteeSelectedSysId } = api.state;
    let currentAdopteeList = [
        ...adopteeList,
    ];
    let currentAdopteeOldName = {};

    // new item
    if (!formData.sys_id) {
        currentAdopteeOldName = {
            _row_data: {
                uniqueValue: `NEW_${new Date().getTime()}`,
            },
            title_name: {
                value: formData.title_name,
            },
            first_name_th: {
                displayValue: formData.first_name_th,
                value: formData.first_name_th
            },
            first_name_en: {
                displayValue: formData.first_name_en,
                value: formData.first_name_en
            },
            last_name_th: {
                displayValue: formData.last_name_th,
                value: formData.last_name_th
            },
            last_name_en: {
                displayValue: formData.last_name_en,
                value: formData.last_name_en
            },
            operation: "insert",
        };

        currentAdopteeList = currentAdopteeList.map( item => {
            if (item._row_data.uniqueValue === adopteeSelectedSysId) {
                return {
                    ...item,
                    oldNameList: [
                        ...item.oldNameList,
                        currentAdopteeOldName
                    ]
                };
            }
            return item;
        });
    } else {
        currentAdopteeList = currentAdopteeList.map( item => {
            if (item._row_data.uniqueValue === adopteeSelectedSysId) {
                return {
                    ...item,
                    oldNameList: item.oldNameList.map( o => {
                        if (o._row_data.uniqueValue === formData.sys_id) {
                            return {
                                _row_data: {
                                    uniqueValue: formData.sys_id,
                                },
                                title_name: {
                                    value: formData.title_name,
                                },
                                first_name_th: {
                                    displayValue: formData.first_name_th,
                                    value: formData.first_name_th
                                },
                                first_name_en: {
                                    displayValue: formData.first_name_en,
                                    value: formData.first_name_en
                                },
                                last_name_th: {
                                    displayValue: formData.last_name_th,
                                    value: formData.last_name_th
                                },
                                last_name_en: {
                                    displayValue: formData.last_name_en,
                                    value: formData.last_name_en
                                },
                                operation: formData.sys_id.includes("NEW_") ? "insert" : "update",
                            };
                        }
                        return o;
                    })
                };
            }
            return item;
        });
    }
    api.setState('adopteeList', currentAdopteeList);
    api.setState('adopteeOldNameFormSubmitted', false);
    api.setState('adopteeOldNameForm', {
        "sys_id": "",
        "first_name_en": "",
        "first_name_th": "",
        "last_name_en": "",
        "last_name_th": "",
        "title_name": "",
    });
    api.emit('MODAL_SELECTED', {
        modalId: 'adoptee_olde_name_modal',
        showModal: false
    });
}
