sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    // form valiation success
    if (event.payload.status === 'success') {
        const { oldNameList, spouseList } = api.state;
        const sysId = event.payload.sysId; // callback sysId

        api.data.save_people_history_old_name.execute({
            peopleInfoSysId: sysId,
            items: oldNameList
        });

        api.data.save_people_spouse.execute({
            peopleInfoSysId: sysId,
            items: spouseList
        });

        api.emit('NAV_ITEM_SELECTED', {
            route: 'people-info',
            fields: {
                sysId: sysId
            }
        });
    }
}
