sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const obj = api.state.oldNameList.find(item => item._row_data.uniqueValue === api.state.oldNameSelectedSysId);
    if (obj) {
        api.setState('oldNameForm', {
            sys_id: obj._row_data.uniqueValue || "",
            title_name: obj.title_name.value || "",
            first_name_en: obj.first_name_en.value || "",
            first_name_th: obj.first_name_th.value || "",
            last_name_en: obj.last_name_en.value || "",
            last_name_th: obj.last_name_th.value || "",
        });
    }
}
