sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const { spouseSelectedSysId, spouseOldNameSelectedSysId, spouseList} = api.state;
    const spouseObj = spouseList.find(item => item._row_data.uniqueValue === spouseSelectedSysId);
    const obj = spouseObj.oldNameList.find(item => item._row_data.uniqueValue === spouseOldNameSelectedSysId);
    if (obj) {
        api.setState('spouseOldNameForm', {
            sys_id: obj._row_data.uniqueValue || "",
            title_name: obj.title_name.value || "",
            first_name_en: obj.first_name_en.value || "",
            first_name_th: obj.first_name_th.value || "",
            last_name_en: obj.last_name_en.value || "",
            last_name_th: obj.last_name_th.value || "",
        });
    }
}
