sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    console.log('delete event called', event);

    const deletedSysId = event.payload.sysId;
    if (!deletedSysId) {
        console.warn('no sys_id in delete event payload');
        return;
    }

    const prev = Array.isArray(api.state.documents)
        ? api.state.documents
        : [];

    // ✅ สร้าง array ใหม่ (immutable)
    const next = prev.filter(doc => doc.table_sys_id !== deletedSysId);

    api.setState('documents', next);

    console.log('documents after delete', next);
}
