sys_ux_client_script.script:
function handler({ api, event }) {
  var el = event && event.elementId;
  if (!el) return;

  var payload = event.payload || {};

  var raw =
    payload.value != null ? payload.value :
    payload.checked != null ? payload.checked :
    payload.selected != null ? payload.selected :
    payload.sysId != null ? payload.sysId :
    '';

  var value = (typeof raw === 'string') ? raw.trim() : raw;

  var MAP = {
    coordinator_email: 'email',
    coordinator_firstname: 'first_name_th',
    coordinator_lastname: 'last_name_th',
    coordinator_tel: 'mobile_no',
    coordinator_prefix: 'title_name'
  };

  var targetKey = MAP[el];
  if (!targetKey) return;

  var current = api.state.coordinatorItem || {};

  api.setState('coordinatorItem', Object.assign({}, current, {
    [targetKey]: value
  }));
  console.log("set co ",api.state.coordinatorItem);
}
