sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const formData = { ...api.state.adopteeForm};
    const fieldName = event.elementId.replace("adoptee_input_", "");

    // select dropdown || input
    const value = event.payload.value || event.payload.fieldValue;

    formData[fieldName] = value;
    api.setState('adopteeForm', formData);
}
