sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const obj = api.state.adopteeList.find(item => item._row_data.uniqueValue === api.state.adopteeSelectedSysId);
    if (obj) {
        api.setState('adopteeForm', {
            sys_id: obj._row_data.uniqueValue || "",
            title_name: obj.title_name.value || "",
            first_name_en: obj.first_name_en.value || "",
            first_name_th: obj.first_name_th.value || "",
            last_name_en: obj.last_name_en.value || "",
            last_name_th: obj.last_name_th.value || "",
            nationality: obj.nationality.value || "",
            id_card: obj.id_card.value || "",
            passport_no: obj.passport_no.value || "",
        });
    }
}
