sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    const results = api.state.nationalityList || [];


    return  results.map(record => {
        return {
            "id": record._row_data.uniqueValue,
            "label": `${record.nationality_th.displayValue}`,
            "sublabel": `${record.nationality_th.displayValue} | ${record.nationality_en.displayValue}`,

        };
    });
}
