sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
}) {
    const obj = api.item.value;

    let title = '';
    if (obj.title_name._reference) {
        title = obj.title_name._reference.title_name_th.value || "";
    } else {
        const objTitle = api.data.look_up_title_name.results.find(item => item._row_data.uniqueValue === obj.title_name.value);
        title = objTitle.title_name_th.displayValue || "";
    }

    const firstName = obj.first_name_th.value || "";
    const lastName = obj.last_name_th.value || "";

    return `${title} ${firstName} ${lastName}`.trim();
}
