sys_ux_client_script.script:
function handler({ api }) {
  var raw = api.state.selectedIdsParam;
  console.log('raw from state =', raw, 'type=', typeof raw);

  var selectedIds = [];

  // 1) ถ้ามันเป็น array อยู่แล้ว
  if (Object.prototype.toString.call(raw) === '[object Array]') {
    selectedIds = raw;
  }
  // 2) ถ้ามันเป็น string (JSON string หรือ URL-encoded JSON)
  else if (typeof raw === 'string' && raw) {
    try {
      selectedIds = JSON.parse(raw);
    } catch (e) {
      try {
        selectedIds = JSON.parse(decodeURIComponent(raw));
      } catch (e2) {
        selectedIds = [];
      }
    }
  }

  api.setState('items', selectedIds);
  console.log('items =', selectedIds);

  console.log('STATE request =', api.state.request);

}
