sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    const {
        isCoordinatorSubmitted,
        coordinatorItem
    } = api.state;
    if (isCoordinatorSubmitted && !coordinatorItem.last_name_th) {
        return [{
            status: "critical",
            content: "กรุณาระบุ",
            icon: "circle-info-outline"
        }];
    }
    return '';
}
