sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
  console.log('rename event called', event);

  const attSysId = event.payload.sys_id;
  const newName  = event.payload.file_name || event.payload.new_file_name;

  if (!attSysId || !newName) {
    console.warn('rename payload missing sys_id or new file name', event.payload);
    return;
  }

  const prev = Array.isArray(api.state.documents) ? api.state.documents : [];

  const next = prev.map(doc =>
    doc.doc_id === attSysId
      ? { ...doc, doc_file: newName }   // ✅ update แบบ immutable
      : doc
  );

  api.setState('documents', next);

  console.log('documents after rename', next);
}
