sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const obj = api.state.lawsuits.find(item => item._row_data.uniqueValue === api.state.lawsuit_selected_sys_id);
    if (obj) {
        api.setState('lawsuits', {
            sys_id: obj._row_data.uniqueValue || "",
            case_id: obj.case_id.value || "",
            case_type: obj.case_type.value || "",
            status: obj.status.value || "",
            court: obj.court.value || "",
            court_date: obj.court_date.value || "",
            accusation: obj.accusation.value || "",
            principal: obj.principal.value || "",
            result: obj.result.value || "",
            is_new: obj.is_new.value || "",
            is_delete: obj.is_delete.value || "",
        });
    }
}
