sys_ux_data_broker_scriptlet.script:
function transform(input) {

    var rows = input.GlideRecord_Query._results;

    var map = {};

    rows.forEach(function (r) {
        var cat = r.form_category;

        if (!map[cat]) {
            map[cat] = {
                category: cat,
                items: []
            };
        }

        map[cat].items.push({
            sys_id: r.number,
            name: r.form_name,
            no: r.form_no,
            type: r.form_type
        });
    });

    var result = [];
    for (var k in map)
        result.push(map[k]);

    return result;
}
