sys_ux_data_broker_scriptlet.script:
(function() {

    var groups = {};

    var gr = new GlideRecord("x_1896436_bot_ap_0_master_form");
    gr.orderBy("form_category");
    gr.query();

    while (gr.next()) {
        var cat = gr.getDisplayValue("form_category");

        if (!groups[cat]) {
            groups[cat] = {
                category: cat,
                items: []
            };
        }

        groups[cat].items.push({
            form_id: gr.getUniqueValue(),
            form_name: gr.getDisplayValue("form_name"),
            form_no: gr.getDisplayValue("form_no"),
            form_type: gr.getValue("form_type")
        });
    }

    var result = [];
    for (var k in groups) {
        result.push(groups[k]);
    }

    return result;

})();
