sys_ux_data_broker_transform.props:
[
  {
    "name": "peopleInfoSysId",
    "label": "People info sysId",
    "description": "People info sysId",
    "readOnly": false,
    "fieldType": "string",
    "required": true
  }
]

sys_ux_data_broker_transform.script:
function transform(input) {
    var peopleInfoSysId = input.peopleInfoSysId;
    if (!peopleInfoSysId) {
        return [];
    }

    var result = [];

    var grPeopleSpouse = new GlideRecord('x_1896436_bot_ap_0_people_spouse');
	grPeopleSpouse.addQuery('people_info_id', peopleInfoSysId);
	grPeopleSpouse.orderBy('sys_created_on');
	grPeopleSpouse.query();

	while (grPeopleSpouse.next()) {
		var spouseSysId = grPeopleSpouse.getUniqueValue();
		var spouseEntry = {
			_row_data: {
				displayValue: grPeopleSpouse.getDisplayValue(),
				uniqueValue: grPeopleSpouse.getUniqueValue()
			},
			title_name: {
				displayValue: grPeopleSpouse.getDisplayValue('title_name'),
				value: grPeopleSpouse.getValue('title_name')
			},
			first_name_th: {
				displayValue: grPeopleSpouse.getDisplayValue('first_name_th'),
				value: grPeopleSpouse.getValue('first_name_th')
			},
			first_name_en: {
				displayValue: grPeopleSpouse.getDisplayValue('first_name_en'),
				value: grPeopleSpouse.getValue('first_name_en')
			},
			last_name_th: {
				displayValue: grPeopleSpouse.getDisplayValue('last_name_th'),
				value: grPeopleSpouse.getValue('last_name_th')
			},
			last_name_en: {
				displayValue: grPeopleSpouse.getDisplayValue('last_name_en'),
				value: grPeopleSpouse.getValue('last_name_en')
			},
			nationality: {
				displayValue: grPeopleSpouse.getDisplayValue('nationality'),
				value: grPeopleSpouse.getValue('nationality')
			},
			id_card: {
				displayValue: grPeopleSpouse.getDisplayValue('id_card'),
				value: grPeopleSpouse.getValue('id_card')
			},
			passport_no: {
				displayValue: grPeopleSpouse.getDisplayValue('passport_no'),
				value: grPeopleSpouse.getValue('passport_no')
			},
			oldNameList: []
		};

		var grOldNames = new GlideRecord('x_1896436_bot_ap_0_people_spouse_history'); 
		grOldNames.addQuery('people_spouse_id', spouseSysId); 
		grOldNames.orderBy('sys_created_on');
		grOldNames.query();

		while (grOldNames.next()) {
			spouseEntry.oldNameList.push({
				_row_data: {
					displayValue: grOldNames.getDisplayValue(),
					uniqueValue: grOldNames.getUniqueValue()
				},
				title_name: {
					displayValue: grOldNames.getDisplayValue('title_name'),
					value: grOldNames.getValue('title_name')
				},
				first_name_th: {
					displayValue: grOldNames.getDisplayValue('first_name_th'),
					value: grOldNames.getValue('first_name_th')
				},
				first_name_en: {
					displayValue: grOldNames.getDisplayValue('first_name_en'),
					value: grOldNames.getValue('first_name_en')
				},
				last_name_th: {
					displayValue: grOldNames.getDisplayValue('last_name_th'),
					value: grOldNames.getValue('last_name_th')
				},
				last_name_en: {
					displayValue: grOldNames.getDisplayValue('last_name_en'),
					value: grOldNames.getValue('last_name_en')
				},
            });
		}
		result.push(spouseEntry);
	}
    return result;
}
