sys_ux_data_broker_transform.props:
[
  {
    "name": "peopleInfoSysId",
    "label": "People info sysId",
    "description": "People info sysId",
    "readOnly": false,
    "fieldType": "string",
    "required": true
  }
]

sys_ux_data_broker_transform.script:
function transform(input) {
    var peopleInfoSysId = input.peopleInfoSysId;
    if (!peopleInfoSysId) {
        return [];
    }

    var result = [];

    var grPeopleAdoptee = new GlideRecord('x_1896436_bot_ap_0_people_adoptee');
	grPeopleAdoptee.addQuery('people_info_id', peopleInfoSysId);
	grPeopleAdoptee.orderBy('sys_created_on');
	grPeopleAdoptee.query();

	while (grPeopleAdoptee.next()) {
		var adopteeSysId = grPeopleAdoptee.getUniqueValue();
		var adopteeEntry = {
			_row_data: {
				displayValue: grPeopleAdoptee.getDisplayValue(),
				uniqueValue: grPeopleAdoptee.getUniqueValue()
			},
			title_name: {
				displayValue: grPeopleAdoptee.getDisplayValue('title_name'),
				value: grPeopleAdoptee.getValue('title_name')
			},
			first_name_th: {
				displayValue: grPeopleAdoptee.getDisplayValue('first_name_th'),
				value: grPeopleAdoptee.getValue('first_name_th')
			},
			first_name_en: {
				displayValue: grPeopleAdoptee.getDisplayValue('first_name_en'),
				value: grPeopleAdoptee.getValue('first_name_en')
			},
			last_name_th: {
				displayValue: grPeopleAdoptee.getDisplayValue('last_name_th'),
				value: grPeopleAdoptee.getValue('last_name_th')
			},
			last_name_en: {
				displayValue: grPeopleAdoptee.getDisplayValue('last_name_en'),
				value: grPeopleAdoptee.getValue('last_name_en')
			},
			nationality: {
				displayValue: grPeopleAdoptee.getDisplayValue('nationality'),
				value: grPeopleAdoptee.getValue('nationality')
			},
			id_card: {
				displayValue: grPeopleAdoptee.getDisplayValue('id_card'),
				value: grPeopleAdoptee.getValue('id_card')
			},
			passport_no: {
				displayValue: grPeopleAdoptee.getDisplayValue('passport_no'),
				value: grPeopleAdoptee.getValue('passport_no')
			},
			oldNameList: []
		};

		var grOldNames = new GlideRecord('x_1896436_bot_ap_0_people_adoptee_history'); 
		grOldNames.addQuery('people_adoptee_id', adopteeSysId); 
		grOldNames.orderBy('sys_created_on');
		grOldNames.query();

		while (grOldNames.next()) {
			adopteeEntry.oldNameList.push({
				_row_data: {
					displayValue: grOldNames.getDisplayValue(),
					uniqueValue: grOldNames.getUniqueValue()
				},
				title_name: {
					displayValue: grOldNames.getDisplayValue('title_name'),
					value: grOldNames.getValue('title_name')
				},
				first_name_th: {
					displayValue: grOldNames.getDisplayValue('first_name_th'),
					value: grOldNames.getValue('first_name_th')
				},
				first_name_en: {
					displayValue: grOldNames.getDisplayValue('first_name_en'),
					value: grOldNames.getValue('first_name_en')
				},
				last_name_th: {
					displayValue: grOldNames.getDisplayValue('last_name_th'),
					value: grOldNames.getValue('last_name_th')
				},
				last_name_en: {
					displayValue: grOldNames.getDisplayValue('last_name_en'),
					value: grOldNames.getValue('last_name_en')
				},
            });
		}
		result.push(adopteeEntry);
	}
    return result;
}
