sys_ux_data_broker_transform.script:
function transform(input) {
	gs.info("[Save people spouse] : " + JSON.stringify(input));
    var items = input.items;
	var peopleInfoSysId = input.peopleInfoSysId;
    var response = {
        success: true,
    };

    if (!items || !Array.isArray(items)) {
        return {
            success: false,
            error: "Invalid input"
        };
    }

	var setSpouseFields = function(gr, item, peopleInfoSysId) {
		gr.setValue('title_name', item.title_name.value || '');
		gr.setValue('first_name_th', item.first_name_th.value || '');
		gr.setValue('first_name_en', item.first_name_en.value || '');
		gr.setValue('last_name_th', item.last_name_th.value || '');
		gr.setValue('last_name_en', item.last_name_en.value || '');
		gr.setValue('nationality', item.nationality.value || '');
		gr.setValue('id_card', item.id_card.value || '');
		gr.setValue('passport_no', item.passport_no.value || '');
		gr.setValue('people_info_id', peopleInfoSysId || '');
	};

    items.forEach(function(item) {
        var gr = new GlideRecord('x_1896436_bot_ap_0_people_spouse');
		var spouseSysId = '';

        if (item.operation === 'insert') {
            gr.initialize();
			setSpouseFields(gr, item, peopleInfoSysId);
            spouseSysId = gr.insert();
        } else if (item.operation === 'update') {
			spouseSysId = item._row_data.uniqueValue;
            if (gr.get(spouseSysId)) {
				setSpouseFields(gr, item, peopleInfoSysId);
                gr.update();
            }
        } else if (item.operation === 'delete') {
            if (gr.get(item._row_data.uniqueValue)) {
                gr.deleteRecord();
            }
        } else {
			spouseSysId = item._row_data.uniqueValue;
		}

		// --- CHILD RECORD LOGIC (1 spouse to Many old name) ---
        if (spouseSysId && item.oldNameList && Array.isArray(item.oldNameList)) {
			var setSpouseOldNameFields = function(gr, item, spouseSysId) {
				gr.setValue('title_name', item.title_name.value || '');
				gr.setValue('first_name_th', item.first_name_th.value || '');
				gr.setValue('first_name_en', item.first_name_en.value || '');
				gr.setValue('last_name_th', item.last_name_th.value || '');
				gr.setValue('last_name_en', item.last_name_en.value || '');
				gr.setValue('people_spouse_id', spouseSysId || '');
			};
            item.oldNameList.forEach(function(childItem) {
                var childGr = new GlideRecord('x_1896436_bot_ap_0_people_spouse_history');

				if (childItem.operation === 'insert') {
					childGr.initialize();
					setSpouseOldNameFields(childGr, childItem, spouseSysId);
					childGr.insert();
				} else if (childItem.operation === 'update') {
					if (childGr.get(childItem._row_data.uniqueValue)) {
						setSpouseOldNameFields(childGr, childItem, spouseSysId);
						childGr.update();
					}
				} else if (childItem.operation === 'delete') {
					if (childGr.get(childItem._row_data.uniqueValue)) {
						childGr.deleteRecord();
					}
				}
            });
        }
    });

    return response;
}
